import { useSelector, useDispatch } from "react-redux";
import { useEffect, useState } from "react";
import { allPinsThunk } from "../../store/pin";
import { Link } from "react-router-dom";
import "./PinsList.css";

const PinsList = () => {
  const [isLoaded, setIsLoaded] = useState(false);
  const dispatch = useDispatch();
  const sessionUser = useSelector((state) => state.session.user);

  useEffect(() => {
    dispatch(allPinsThunk()).then(() => setIsLoaded(true));
  }, [dispatch]);

  let pinsState = useSelector((state) => state.pins);

  let PINS;
  if (isLoaded) {
    PINS = Object.values(pinsState);
  }

  return (
    <>
      <div>
        {sessionUser && sessionUser.id ? (
          <div className="products-list-yellow">
            <h2 className="products-list-title">
              Welcome!
            </h2>
          </div>
        ) : (
          <div className="products-list-blue">
          <h2 className="products-list-title">
            Incredible style and decor, plus one-of-a-kind gifts right this way!
          </h2>
          </div>
        )}
      </div>
      <div className="products-list-grid">
        {PINS &&
          PINS.map((pin) => (
            <div
              className="products-list-products"
              key={pin.id}
              pin={pin}
            >
              <Link to={`/products/${pin.id}`}>
                <img
                  className="products-list-image"
                  src={pin.imageURL || "http://www.rcdrilling.com/wp-content/uploads/2013/12/default_image_01-1024x1024-570x760.png"}
                  alt="img"
                />
              </Link>
            </div>
          ))}
      </div>
    </>
  );
};

export default PinsList;
