const initialState = {};

const pinReducer = (state = initialState, action) => {
  switch (action.type) {
    case GET_PINS:
     let newState = {} ;
      action.pins.pins.forEach((pin) => {
        console.log('#####',pin)
        newState[pin.id] = pin;
      });
      return newState;
    default:
      return state;
  }
};

export default pinReducer;
