import "./CommentCard.css";
import { useParams } from "react-router-dom";
import { useSelector, useDispatch } from "react-redux";
import { deleteCommentThunk } from "../../store/comment";
import OpenModalButton from "../OpenModalButton";
import EditCommentForm from "../EditCommentForm";

const CommentCard = ({ comment }) => {
  const { pinId } = useParams();
  const userState = useSelector((state) => state.session);

  const dispatch = useDispatch();

//   if (comment.user.id === userState.user?.id) {
//     setHasComment(true);
//   }

  const editCommentInfo = () => {
    if (userState.user && comment && userState.user.id === comment.user.id) {
      return (
        <OpenModalButton
          buttonText="Edit Your Comment"
          modalComponent={
            <EditCommentForm comment={comment} pinId={pinId} />
          }
        />
      );
    }
  };

  const commentDeleter = () => {
    const confirm = window.confirm(
      `Are you sure you wish to delete your comment?`
    );
    if (confirm) {
      dispatch(deleteCommentThunk(pinId, comment)).then(() => {
        setHasComment(false);
      });
    }
  };

  const deleteComment = (e) => {
    if (userState.user && comment && userState.user.id === comment.user.id) {
      return (
        <button
          className="comment-card-delete-button"
          onClick={() => {
            commentDeleter();
          }}
        >
          Delete Comment
        </button>
      );
    }
  };

  return (
    <div>
      <div className="pin-page-comment-spacing">
        <div className="comment-card-comment-body">{comment.body}</div>
        <div className="pin-page-user-img-name-container">
          {" "}
          <div className="comment-card-user-name">{comment.user.username}</div>
        </div>
        <div className="pin-page-edit-comment-buttons">
          {editCommentInfo()}
          {deleteComment()}
        </div>
      </div>
    </div>
  );
};

export default CommentCard;
