import "./CreatePinForm.css";
import { useEffect, useState } from "react";
import { useDispatch, useSelector } from "react-redux";
import { allPinsThunk,makePinThunk } from "../../store/pin";
import { useHistory } from "react-router-dom";

const CreatePinForm = () => {
  const dispatch = useDispatch();
  const sessionUser = useSelector((state) => state.session.user);
  const history = useHistory();

  const [title, setTitle] = useState("");
  const [description, setDescription] = useState("");
  const [image_URL, setImageURL] = useState("");
  const [errors, setErrors] = useState([]);

  const updateTitle = (e) => setTitle(e.target.value);
  const updateDescription = (e) => setDescription(e.target.value);
  const updateImageURL = (e) => setImageURL(e.target.value);

  useEffect(() => {
    dispatch(allPinsThunk());
  }, [dispatch]);

  let createdPin;

  const handleSubmit = async (e) => {
    e.preventDefault();

    const payload = {
      title,
      description,
      image_URL,
    };

    createdPin = await dispatch(makePinThunk(payload))
    if (createdPin && createdPin.id) {
      history.push(`/pins/${createdPin.id}`)
    } else {
      setErrors(createdPin);
    }

  };


  const errorHandle = () => {
    if (errors.length > 0) {
      return (
        <div className='createPin-errors-container'>
          {errors.map((error, index) => (
            <li className='createProduct-errors' key={index}>Error occurred - {error} </li>
          ))}
        </div>

      )
    }
  }


  return sessionUser.id ? (
    <section className="create-product-form">
      <form onSubmit={handleSubmit}>
        {errorHandle()}

        <div className='createProduct-field'>
          <div className='createProduct-keys'>
            <h4>Add your title</h4>
          </div>
          <input
            className='createProduct-input'
            type="text"
            placeholder="Add your title"
            value={title}
            onChange={updateTitle}
          />
        </div>

        <div className='createProduct-field'>
        <div className='createProduct-keys'>
            <h4>Tell everyone what your pin is about</h4>
          </div>
          <textarea
            className='createProduct-input-description'
            type="text"
            placeholder="Tell everyone what your pin is about"
            value={description}
            onChange={updateDescription}
          />
        </div>


        <div className='createProduct-field'>
        <div className='createProduct-keys'>
            <h4>Image URL</h4>
          </div>
          <input
            className='createProduct-input'
            type="text"
            placeholder="Image url here"
            value={image_URL}
            onChange={updateImageURL}
          />
        </div>

        <div className='createProduct-save-container'>
          <button className='createProduct-save' type="submit">Save</button>
        </div>
      </form>
    </section>
  ) : null;
};

export default CreatePinForm;
